(function($){"use strict";wp.customize.bind("ready",function(){wp.customize.panel("xt_woovs-archives",function(section){section.expanded.bind(function(isExpanding){if(isExpanding){var current_url=wp.customize.previewer.previewUrl();if(!woovs_controls.is_shop&&current_url!==woovs_controls.archive_url){wp.customize.previewer.previewUrl(woovs_controls.archive_url)}}})});wp.customize.panel("xt_woovs-single",function(section){section.expanded.bind(function(isExpanding){if(isExpanding){var current_url=wp.customize.previewer.previewUrl();if(current_url!==woovs_controls.single_url&&wp.customize.previewer.preview&&!$(wp.customize.previewer.preview.targetWindow().document).find("body").hasClass("single-product")){wp.customize.previewer.previewUrl(woovs_controls.single_url)}}})})})})(jQuery);